graphql.query:
mutation {
    createProject(
        label: "Orbis",
        description: "PS4"
    ) {
        id
        label
        description
    }
}

query {
    getProjectsPage(pageIndex: 0, pageSize: 10) {
        content {
            id
            label
            description
        }
        pageIndex
        pageSize
        totalElements
        hasPrevious
        hasNext
    }
}

