graphql.query:
mutation {
    createProject(
        input: {
            label: "Orbis",
            description: "PS4"
        }
    ) {
        id
        label
        description
    }
}

query {
    getProjectsPage(
        pagination: { index: 0, size: 10 }
    ) {
        content {
            id
            label
            description
        }
        pageIndex
        pageSize
        totalElements
        hasPrevious
        hasNext
    }
}

query {
    searchProjects(
        query: "description:ps4", 
        pagination: { index: 0, size: 10 }
    ) {
        content {
            id
            label
            description
        }
        pageIndex
        pageSize
        totalElements
        hasPrevious
        hasNext
    }
}

